import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { IonicModule } from '@ionic/angular';
import { ActivatedRoute } from '@angular/router';
import { GfncElement, GfncService } from '../services/gfnc.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.page.html',
  styleUrls: ['./details.page.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, FormsModule]
})
export class DetailsPage implements OnInit {

  characterId!: number;
  character: GfncElement | null = null;

  constructor(private route: ActivatedRoute, private gfncService: GfncService) {}

  async ngOnInit() {
    const id = this.route.snapshot.paramMap.get('id');

    if (id) {
      this.characterId = +id;  // Convierte el ID a número
      try {
        // Obtener los detalles del personaje
        this.character = await this.gfncService.getById(this.characterId);
        console.log('Character details:', this.character);
      } catch (error) {
        console.error('Error al cargar los detalles del personaje:', error);
      }
    } else {
      console.error('ID no encontrado en la ruta');
    }
  }
  console.log('Character details:', this.character);

}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-back-button defaultHref="/gfnc"></ion-back-button>
    </ion-buttons>
    <ion-title>Detalles</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content *ngIf="character">
  <div class="character-details">
    <img [src]="character.Imagen" [alt]="character.Nombre" />
    <h1>{{ character.Nombre }}</h1>
    <p><b>Clase:</b> {{ character.Clase }}</p>
    <p><b>Rareza:</b> {{ character.Rareza }}</p>

  </div>
</ion-content>

<ion-content *ngIf="!character">
  <p>Cargando detalles...</p>
</ion-content>
