import { Component, inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { IonicModule } from '@ionic/angular';
import { GfncElement, GfncService } from '../services/gfnc.service';

@Component({
  selector: 'app-gfnc',
  templateUrl: './gfnc.page.html',
  styleUrls: ['./gfnc.page.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, FormsModule]
})
export class GfncPage implements OnInit {

  gfncall: GfncElement[] = []

  gfncService = inject(GfncService);

  errorMessage: string | null = null; 

  constructor() { }

  async ngOnInit() {
    try {
      const response = await this.gfncService.getAll();
      this.gfncall = response.gfnc;
    } catch (error) {
      console.error('Error al cargar los productos:', error);
      this.errorMessage = 'Hubo un problema al cargar los productos. Inténtalo de nuevo más tarde.';
    }
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button menu="m1"></ion-menu-button>
    </ion-buttons>
    <ion-title>GFNC</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">GFNC</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-list>
    <ion-list-header>
      <ion-label>Personajes</ion-label>
    </ion-list-header>

    <!-- Si hay un error, mostramos un mensaje -->
    <div *ngIf="errorMessage" class="error-message">
      <p>{{ errorMessage }}</p>
    </div>

    <!-- Si no hay error, mostramos los personajes -->

    <ng-container *ngIf="!errorMessage">
      <ion-grid>
        <ion-row>
          <ion-col size="6" size-md="4" size-lg="3" *ngFor="let caracther of gfncall">
            <ion-card class="custom-card" (click)="goToDetails(caracther.id)">
              <img [src]="caracther.Imagen" [alt]="caracther.Nombre" />
              <ion-card-header>
                <ion-card-title>{{ caracther.Nombre }}</ion-card-title>
                <ion-card-subtitle><b>Clase:</b>  {{ caracther.Clase }} </ion-card-subtitle>
                <ion-card-subtitle><b>Rareza:</b> {{ caracther.Rareza }}</ion-card-subtitle>
              </ion-card-header>
            </ion-card>
          </ion-col>
        </ion-row>
      </ion-grid>
    </ng-container>

  </ion-list>
</ion-content>
